import React from 'react';

import Mask from '../Mask/index';

import { ItemProps, ItemWithMaskProps } from './types';
import { SC_Item_Wrapper, SC_Photo } from './index.sc';

export const Item: React.FC<ItemProps> = (props) => {
    const { source, itemWidth, itemHeight } = props;

    return (
        <SC_Item_Wrapper>
            <SC_Photo source={source} height={itemHeight} />
        </SC_Item_Wrapper>
    );
}

export const ItemWithMask: React.FC<ItemWithMaskProps> = (props) => {
    const { source, itemWidth, itemHeight, remainingPhotos } = props;

    return (
        <SC_Item_Wrapper>
            <Mask remainingPhotos={remainingPhotos} />
            <SC_Photo source={source} itemHeight={itemHeight} />
        </SC_Item_Wrapper>
    );
}

export default Item;